import { useQuery } from '@apollo/client';
import { abis, addresses } from '@dex/contracts';
import { formatUnits, parseEther, parseUnits } from '@ethersproject/units';
import { useCall, useContractFunction, useDebounce, useEthers, useToken } from '@usedapp/core';
import { BigNumber } from 'ethers';
import React, { useCallback, useEffect, useState } from 'react';
import { RiArrowUpDownLine } from 'react-icons/ri';
import { getContract, prettyNum } from '../../common/utils';
import { useLiquidityReserve } from '../../hooks';

const calcAmountOut = (amountIn, rIn, rOut) => {
  const inWithFee = parseUnits(BigNumber.from(amountIn).toString(), 18).mul(997);
  return rOut.mul(inWithFee).div(rIn.mul(1000).add(inWithFee));
};

const calcAmountIn = (amountOut, rIn, rOut) => {
  return rIn.mul(amountOut).mul(1000).div(rOut.sub(amountOut).mul(997));
};

function useRouterContractFunctionCall(routerAddress, method) {
  const { value, error } =
    useCall(
      routerAddress && {
        contract: getContract(abis.router, routerAddress),
        method: method,
        args: [],
      }
    ) ?? {};
  if (error) {
    console.error(error.message);
    return undefined;
  }
  return value?.[0];
}

export const Swap = ({ token1Address, token2Address, swapPosition }) => {
  const token1 = useToken(token1Address);
  const token2 = useToken(token2Address);
  const { library } = useEthers();
  const routerContract = getContract(abis.router, addresses[4].router, library);
  const [[input1, input2], setInput] = useState([0, 0]);
  const [[output1, output2], setOutput] = useState([0, 0]);
  const { active, r0, r1 } = useLiquidityReserve(addresses[4].pair);
  const debouncedValue1 = useDebounce(input1, 1000);
  const debouncedValue2 = useDebounce(input2, 1000);
  const [exchangePrice, setExchangePrice] = useState(0);

  const getAmountIn = useCallback(
    async (amountOut) => {
      if (r0 && r1) {
        return await routerContract.getAmountIn(parseEther(amountOut), r0, r1);
      }
    },
    [r0, r1, routerContract]
  );
  const getAmountOut = useCallback(
    async (amountIn) => {
      if (r0 && r1) {
        return await routerContract.getAmountOut(parseEther(amountIn), r0, r1);
      }
      return undefined;
    },
    [r0, r1, routerContract]
  );

  useEffect(() => {
    if (debouncedValue1) {
      getAmountOut(debouncedValue1).then((data) => {
        setInput((prv) => [prv[0], 0]);
        setOutput((prvState) => [prvState[0], data]);
      });
    }
  }, [debouncedValue1, getAmountOut, r0, r1]);

  useEffect(() => {
    if (debouncedValue2) {
      getAmountIn(debouncedValue2).then((data) => {
        setInput((prv) => [0, prv[1]]);
        setOutput((prvState) => [data, prvState[1]]);
      });
    }
  }, [debouncedValue2, getAmountIn, r0, r1]);

  useEffect(() => {
    if (r0 && r1 && token1 && token2)
      getAmountIn(1).then((value) => setExchangePrice(`1 ${token1.symbol} = ${value} ${token2.symbol}`));
  }, [r0, r1, token1, token2, getAmountOut]);

  const token1InputHandle = (ev) => {
    setInput((prvState) => [ev.target.value, prvState[1]]);
    setOutput([0, 0]);
  };

  const token2InputHandle = (ev) => {
    setInput((prvState) => [prvState[0], ev.target.value]);
    setOutput([0, 0]);
  };

  return active && token1 && token2 ? (
    <form className="flex flex-col ">
      <h1 className="text-[32px] text-center mt-6 mb-2 font-bold">SWAP</h1>

      <div className="flex flex-row space-x-2 my-2 ">
        <label className="w-28">
          {token1.name} ({token1.symbol}):{' '}
        </label>
        <input
          className="border border-gray-400 rounded flex-grow px-2 py-1"
          value={input1 ? input1 : prettyNum(output1, token1.decimals)}
          type="number"
          onChange={token1InputHandle}
        />
      </div>
      <button
        className="rounded-full border border-gray-300 hover:bg-gray-200 w-6 h-6 flex justify-center items-center mx-auto"
        onClick={swapPosition}
      >
        <RiArrowUpDownLine />
      </button>
      <div className="flex flex-row space-x-2 my-2 ">
        <label className="w-28">
          {token2.name} ({token2.symbol}):{' '}
        </label>
        <input
          className="border border-gray-400 rounded flex-grow px-2 py-1"
          value={input2 ? input2 : prettyNum(output2, token2.decimals)}
          type="number"
          onChange={token2InputHandle}
        />
      </div>
      <p className="text-gray-600 text-center">1 ETH = 20</p>
      <button
        className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2 w-full"
        onClick={() => {}}
      >
        Swap
      </button>
    </form>
  ) : (
    <p>Loading...</p>
  );
};
