import { useQuery } from '@apollo/client';
import { Contract } from '@ethersproject/contracts';
import { shortenAddress, useCall, useEthers, useLookupAddress } from '@usedapp/core';
import React, { useEffect, useState } from 'react';

import { Body, Button, Container, Header, Image, Link } from './components';
import logo from './ethereumLogo.png';

import { MAINNET_ID, addresses, abis } from '@uniswap-v2-app/contracts';
import GET_AGGREGATED_UNISWAP_DATA from './graphql/subgraph';

function WalletButton() {
  const [rendered, setRendered] = useState('');

  const ens = useLookupAddress();
  const { account, activateBrowserWallet, deactivate, error } = useEthers();

  useEffect(() => {
    if (ens) {
      setRendered(ens);
    } else if (account) {
      setRendered(shortenAddress(account));
    } else {
      setRendered('');
    }
  }, [account, ens, setRendered]);

  useEffect(() => {
    if (error) {
      console.error('Error while connecting wallet:', error.message);
    }
  }, [error]);
  return rendered ? (
    <div className="flex flex-col items-center justify-center">
      <h1 className="text-lg">Network</h1>
    </div>
  ) : (
    <button
      className="hover:bg-blue-200 hover:font-bold border border-blue-500 rounded px-2 py-1 m-2 w-full"
      onClick={() => {
        if (!account) {
          activateBrowserWallet();
        } else {
          deactivate();
        }
      }}
    >
      Connect wallet
    </button>
  );
}

function App() {
  // Read more about useDapp on https://usedapp.io/

  return (
    <div className="w-[480px] mx-auto px-4">
      <div className="flex flex-col items-center"></div>
      <WalletButton />

      <form className="flex flex-col align">
        <div className="flex flex-row space-x-2">
          <label>ONE: </label>
          <input className="border border-grey-400 rounded" />
        </div>
        <label>TWO: </label>
        <input />
      </form>
    </div>
  );
}

export default App;
