import { shortenAddress, useCall, useEthers, useLookupAddress, useEtherBalance } from '@usedapp/core';
import React, { useEffect, useState } from 'react';
import { Swap } from './components/Swap';

import { formatEther } from '@ethersproject/units';
function WalletButton() {
  const { account, activateBrowserWallet, deactivate, error } = useEthers();

  useEffect(() => {
    if (error) {
      console.error('Error while connecting wallet:', error.message);
      alert(error.message);
    }
  }, [error]);
  return account ? (
    <button
      className="hover:bg-red-200 hover:font-bold border border-red-500 text-red-500 rounded px-2 py-1 my-2 "
      onClick={() => {
        deactivate();
      }}
    >
      Disconnect
    </button>
  ) : (
    <button
      className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2"
      onClick={() => {
        activateBrowserWallet();
      }}
    >
      Connect
    </button>
  );
}

function App() {
  const [rendered, setRendered] = useState('');
  const ens = useLookupAddress();
  const { account, error } = useEthers();
  const balance = useEtherBalance(account);
  useEffect(() => {
    if (ens) {
      setRendered(ens);
    } else if (account) {
      setRendered(shortenAddress(account));
    } else {
      setRendered('');
    }
  }, [account, ens, setRendered]);

  return (
    <div className="w-[480px] mx-auto px-4">
      <div className="flex items-start justify-between">
        {rendered ? (
          <div className="flex flex-col items-start">
            {account && <p>Account: {rendered}</p>}
            {balance && <p>Ether balance: {formatEther(balance)} ETH </p>}
          </div>
        ) : (
          <span></span>
        )}
        <WalletButton />
      </div>

      <hr />
      <Swap />
    </div>
  );
}

export default App;
