import { useQuery } from '@apollo/client';
import { Contract } from '@ethersproject/contracts';
import { shortenAddress, useCall, useEthers, useLookupAddress } from '@usedapp/core';
import React, { useEffect, useState } from 'react';

function WalletButton() {
  const { account, activateBrowserWallet, deactivate } = useEthers();

  return account ? (
    <button
      className="hover:bg-red-200 hover:font-bold border border-red-500 text-red-500 rounded px-2 py-1 my-2 w-full"
      onClick={() => {
        deactivate();
      }}
    >
      Disconnect
    </button>
  ) : (
    <button
      className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2 w-full"
      onClick={() => {
        activateBrowserWallet();
      }}
    >
      Connect
    </button>
  );
}

function App() {
  const [accountEns, setAccountEns] = useState();
  const ens = useLookupAddress();
  const { account, activateBrowserWallet, deactivate, error } = useEthers();
  useEffect(() => {
    if (ens) {
      setRendered(ens);
    } else if (account) {
      setRendered(shortenAddress(account));
    } else {
      setRendered('');
    }
  }, [account, ens, setRendered]);

  useEffect(() => {
    if (error) {
      console.error('Error while connecting wallet:', error.message);
      alert(error.message);
    }
  }, [error]);

  return (
    <div className="w-[480px] mx-auto px-4">
      <div className="flex flex-col items-center"></div>
      <WalletButton />

      <form className="flex flex-col align">
        <div className="flex flex-row space-x-2">
          <label>ONE: </label>
          <input className="border border-grey-400 rounded" />
        </div>
        <label>TWO: </label>
        <input />
      </form>
    </div>
  );
}

export default App;
