import { useQuery } from '@apollo/client';
import { addresses } from '@dex/contracts';
import { useCall, useContractFunction, useDebounce, useToken } from '@usedapp/core';
import React, { useEffect, useState } from 'react';
import { RiArrowUpDownLine } from 'react-icons/ri';
import { useLiquidityReserve } from '../../hooks';

const calcAmountOut = (amountIn, rIn, rOut) => {
  const inWithFee = amountIn.mul(997);
  return rOut.mul(inWithFee).div(rIn.mul(1000).add(inWithFee));
};

const calcAmountIn = (amountOut, rIn, rOut) => {
  return rIn.mul(amountOut).mul(1000).div(rOut.sub(amountOut).mul(997));
};

export const Swap = ({ token1Address, token2Address, swapPosition }) => {
  const token1 = useToken(token1Address);
  const token2 = useToken(token2Address);
  const [[value1, value2], setDisplayValue] = useState([0, 0]);
  const { active, r0, r1 } = useLiquidityReserve(addresses[4].pair);
  const debouncedValue1 = useDebounce(value1, 1000);
  const debouncedValue2 = useDebounce(value2, 1000);

  useEffect(() => {
    if (debouncedValue1) {
      setDisplayValue((prvState) => [prvState[0], calcAmountOut(debouncedValue1, r0, r1)]);
    }
  }, [debouncedValue1]);

  useEffect(() => {
    if (debouncedValue2) {
      setDisplayValue((prvState) => [calcAmountIn(debouncedValue2, r0, r1), prvState[1]]);
    }
  }, [debouncedValue2]);

  const token1InputHandle = (ev) => {
    setDisplayValue((prvState) => [ev.target.value, prvState[1]]);
  };

  return token1 && token2 ? (
    <form className="flex flex-col ">
      <h1 className="text-[32px] text-center mt-6 mb-2 font-bold">SWAP</h1>

      <div className="flex flex-row space-x-2 my-2 ">
        <label className="w-24">{token1.symbol}: </label>
        <input className="border border-gray-400 rounded flex-grow" value={value1} type="number" />
      </div>
      <button
        className="rounded-full border border-gray-300 hover:bg-gray-200 w-6 h-6 flex justify-center items-center mx-auto"
        onClick={swapPosition}
      >
        <RiArrowUpDownLine />
      </button>
      <div className="flex flex-row space-x-2 my-2 ">
        <label className="w-24">{token2.symbol}: </label>
        <input className="border border-gray-400 rounded flex-grow" value={value2} type="number" />
      </div>
      <p className="text-gray-600 text-center">1 ETH = 20</p>
      <button
        className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2 w-full"
        onClick={() => {}}
      >
        Swap
      </button>
    </form>
  ) : (
    <p>Loading...</p>
  );
};
