import { useQuery } from '@apollo/client';
import { Contract } from '@ethersproject/contracts';
import { shortenAddress, useCall, useEthers, useLookupAddress } from '@usedapp/core';
import React, { useEffect, useState } from 'react';
import { RiArrowUpDownLine } from 'react-icons/ri';

function WalletButton() {
  const { account, activateBrowserWallet, deactivate } = useEthers();

  return account ? (
    <button
      className="hover:bg-red-200 hover:font-bold border border-red-500 text-red-500 rounded px-2 py-1 my-2 "
      onClick={() => {
        deactivate();
      }}
    >
      Disconnect
    </button>
  ) : (
    <button
      className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2"
      onClick={() => {
        activateBrowserWallet();
      }}
    >
      Connect
    </button>
  );
}

function App() {
  const [accountEns, setAccountEns] = useState('');
  const ens = useLookupAddress();
  const { account, activateBrowserWallet, deactivate, error } = useEthers();
  useEffect(() => {
    if (ens) {
      setAccountEns(ens);
    } else if (account) {
      setAccountEns(shortenAddress(account));
    } else {
      setAccountEns('');
    }
  }, [account, ens, setAccountEns]);

  useEffect(() => {
    if (error) {
      console.error('Error while connecting wallet:', error.message);
      alert(error.message);
    }
  }, [error]);

  return (
    <div className="w-[480px] mx-auto px-4">
      <div className="flex flex-col items-center"></div>
      <WalletButton />

      <form className="flex flex-col align">
        <div className="flex flex-row space-x-2 my-2 ">
          <label>TOKEN ONE: </label>
          <input className="border border-gray-800 rounded flex-grow" />
        </div>
        <button className="rounded-full border border-gray-800 w-6 h-6 flex justify-center items-center">
          <RiArrowUpDownLine />
        </button>
        <div className="flex flex-row space-x-2 my-2">
          <label>TOKEN TWO: </label>
          <input className="border border-gray-800 rounded flex-grow" />
        </div>

        <button
          className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2 w-full"
          onClick={() => {}}
        >
          Swap
        </button>
      </form>
    </div>
  );
}

export default App;
