import React, { useEffect, useState } from 'react';

export const TransactionButton = ({ onClick, label, className, state }) => {
  const [rendered, setRendered] = useState(label);
  useEffect(() => {
    if (!state || state.status === 'None') return;
    if (state.status === 'PendingSignature') {
      setRendered();
    }
  }, [state]);

  const loadingBtn = (content) => (
    <button
      className={`bg-blue-500 text-white hover:bg-blue-300 rounded px-2 py-1.5 opacity-30 ${className}`}
      onClick={onClick}
    >
      {content}
    </button>
  );

  const normalButton = () => {
    return (
      <button className={`bg-blue-500 text-white hover:bg-blue-300 rounded px-2 py-1.5 ${className}`} onClick={onClick}>
        {rendered}
      </button>
    );
  };
  return (
    <button className={`bg-blue-500 text-white hover:bg-blue-300 rounded px-2 py-1.5 ${className}`} onClick={onClick}>
      {rendered}
    </button>
  );
};
