import React, { useEffect, useState } from 'react';

export const TransactionButton = ({ onClick, label, className, state }) => {
  const [rendered, setRendered] = useState(normalButton());
  useEffect(() => {
    if (!state || state.status === 'None') return;
    if (state.status === 'PendingSignature') setRendered(loadingBtn('Pending'));
    else if (state.status === 'Mining') setRendered(loadingBtn('Mining'));
    else if (state.status === 'Success') setRendered(normalButton());
  }, [state]);

  const loadingBtn = (content) => (
    <button className={`bg-blue-500 text-white hover:bg-blue-300 rounded px-2 py-1.5 opacity-30 ${className}`} disabled>
      <svg
        class="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
        xmlns="http://www.w3.org/2000/svg"
        fill="none"
        viewBox="0 0 24 24"
      >
        <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
        <path
          class="opacity-75"
          fill="currentColor"
          d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
        ></path>
      </svg>
      {content}
    </button>
  );

  const normalButton = () => {
    return (
      <button className={`bg-blue-500 text-white hover:bg-blue-300 rounded px-2 py-1.5 ${className}`} onClick={onClick}>
        {label}
      </button>
    );
  };
  return { rendered };
};
