import { shortenAddress, useCall, useEthers, useLookupAddress } from '@usedapp/core';
import React, { useEffect, useState } from 'react';
import { Swap } from './components';

function WalletButton() {
  const { account, activateBrowserWallet, deactivate } = useEthers();

  return account ? (
    <button
      className="hover:bg-red-200 hover:font-bold border border-red-500 text-red-500 rounded px-2 py-1 my-2 "
      onClick={() => {
        deactivate();
      }}
    >
      Disconnect
    </button>
  ) : (
    <button
      className="hover:bg-blue-200 hover:font-bold border border-blue-500 text-blue-500 rounded px-2 py-1 my-2"
      onClick={() => {
        activateBrowserWallet();
      }}
    >
      Connect
    </button>
  );
}

function App() {
  const [accountEns, setAccountEns] = useState('');
  const ens = useLookupAddress();
  const { account, activateBrowserWallet, deactivate, error } = useEthers();
  useEffect(() => {
    if (ens) {
      setAccountEns(ens);
    } else if (account) {
      setAccountEns(shortenAddress(account));
    } else {
      setAccountEns('');
    }
  }, [account, ens, setAccountEns]);

  useEffect(() => {
    if (error) {
      console.error('Error while connecting wallet:', error.message);
      alert(error.message);
    }
  }, [error]);

  return (
    <div className="w-[480px] mx-auto px-4">
      <div className="flex flex-col items-center"></div>
      <WalletButton />

      <hr />
      <Swap />
    </div>
  );
}

export default App;
