import { useToken } from '@usedapp/core';

import { formatUnits } from '@ethersproject/units';
import React from 'react';
import { prettyNum } from '../../common/utils';

export const Token = ({ title, address }) => {
  const token = useToken('0x95b58a6bff3d14b7db2f5cb5f0ad413dc2940658');
  // if (!address) return;

  return token ? (
    <div className="flex flex-col">
      <h1 className="text-[32px] text-center mt-6 font-bold">TOKEN {title}</h1>
      <p>Token name: {token?.name}</p>
      <p>Token symbol: {token?.symbol}</p>
      <p>Token decimals: {token?.decimals}</p>
      <p>Token totalSupply: {token?.totalSupply ? prettyNum(token?.totalSupply, token?.decimals) : ''}</p>
    </div>
  ) : (
    <>Loading...</>
  );
};
